/*
 * grunt-comment-media-queries
 * https://github.com/stephband/grunt.comment-media-queries
 */

'use strict';

module.exports = function(grunt) {
	var rmedia = /^\s*@media\s*(print)?/;

	// Please see the Grunt documentation for more information regarding task
	// creation: http://gruntjs.com/creating-tasks

	grunt.registerMultiTask('comment-media-queries', 'Comment out media queries.', function() {

		// Iterate over all specified file groups.
		this.files.forEach(function(f) {
			// Concat specified files.
			var src = f.src.filter(function(filepath) {
				// Warn on and remove invalid source files (if nonull was set).
				if (!grunt.file.exists(filepath)) {
					grunt.log.warn('Source file "' + filepath + '" not found.');
					return false;
				} else {
					return true;
				}
			}).map(function(filepath) {
				var array = grunt.file.read(filepath).split('\n'),
					count = 0,
					print = false;

				return array.map(function(line) {
					var test = rmedia.exec(line);
console.log(test);
					if (test) {
						++count;

						if (test[1]) {
							print = true;
						}

						return '/* ' + line + ' */';
					}

					if (count) {
						line = line.replace(/\{|\}/g, function($0, $1) {
							if ($0 === '{') {
								++count;
								return $0;
							}
							else {
								--count;
								
								if (count === 0) {
									print = false;
									return '/* ' + $0 + ' */'; 
								}

								return $0 ;
							}
						});

						return print ? '/*' + line + '*/' : line ;
					}

					return line;
				}).join('\n');
			}).join('\n');

			// Write the destination file.
			grunt.file.write(f.dest, src);

			// Print a success message.
			grunt.log.writeln('File "' + f.dest + '" created.');
		});
	});
};
